--
-- Table structure for table `wp_posts`
--

DROP TABLE IF EXISTS `wp_posts`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `wp_posts` (
  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `post_author` bigint(20) unsigned NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext NOT NULL,
  `post_title` text NOT NULL,
  `post_excerpt` text NOT NULL,
  `post_status` varchar(20) NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) NOT NULL DEFAULT 'open',
  `post_password` varchar(20) NOT NULL DEFAULT '',
  `post_name` varchar(200) NOT NULL DEFAULT '',
  `to_ping` text NOT NULL,
  `pinged` text NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext NOT NULL,
  `post_parent` bigint(20) unsigned NOT NULL DEFAULT '0',
  `guid` varchar(255) NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0',
  PRIMARY KEY (`ID`),
  KEY `post_name` (`post_name`),
  KEY `type_status_date` (`post_type`,`post_status`,`post_date`,`ID`),
  KEY `post_parent` (`post_parent`),
  KEY `post_author` (`post_author`)
) ENGINE=InnoDB AUTO_INCREMENT=240 DEFAULT CHARSET=utf8;
/*!40101 SET character_set_client = @saved_cs_client */;

--
-- Dumping data for table `wp_posts`
--

LOCK TABLES `wp_posts` WRITE;
/*!40000 ALTER TABLE `wp_posts` DISABLE KEYS */;
INSERT INTO `wp_posts` VALUES (1,1,'2013-11-06 16:15:46','2013-11-06 16:15:46','Bem-vindo ao WordPress. Esse é o seu primeiro post. Edite-o ou exclua-o, e então comece a publicar!','Olá, mundo!','','trash','open','open','','ola-mundo','','','2013-12-12 17:30:16','2013-12-12 17:30:16','',0,'http://localhost/adm/blog/?p=1',0,'post','',1),(2,1,'2013-11-06 16:15:46','2013-11-06 16:15:46','&nbsp;\r\n\r\n<a href=\"http://localhost/adm/blog/wp-content/uploads/2013/11/blog.jpg\"><img class=\"size-medium wp-image-36 aligncenter\" alt=\"blog\" src=\"http://localhost/adm/blog/wp-content/uploads/2013/11/blog-300x116.jpg\" width=\"300\" height=\"116\" /></a>\r\n\r\nA ADM Soluções, empresa júnior do curso de Administração da Universidade Estadual do Ceará, foi constituída em 1992, sendo a pioneira no estado. Está há 21 anos no mercado proporcionando aos seus membros desenvolvimento profissional, pelo alinhamento do conhecimento teórico à prática; possibilitando aos seus clientes, satisfação; e elevando o nome da UECE no meio acadêmico e empresarial.\r\n\r\nAtualmente, oferece soluções Organizacionais, em Marketing, em Finanças e em Planejamento, além de desenvolver projetos educacionais, como o \"Atualize-se\" que tem como objetivo capacitar os graduandos em administração da UECE. No mercado, a ADM Soluções já desenvolveu trabalhos para empresas como Mililo Cosméticos, M&amp;M Panificadora, Prime Remoções, NS Cosmétics, COELCE, Centro Cultural Dragão do Mar, Tribunal de Contas do Estado, Hilpro Idiomas, Expresso Guanabara, Vision Laser, Pró-Teste etc.','Quem somos','','publish','open','open','','pagina-exemplo','','','2013-12-12 16:58:51','2013-12-12 16:58:51','',0,'http://localhost/adm/blog/?page_id=2',0,'page','',0),(4,1,'2013-11-20 15:39:27','2013-11-20 15:39:27','Esta é uma página de exemplo. É diferente de um post porque ela ficará em um local e será exibida na navegação do seu site (na maioria dos temas). A maioria das pessoas começa com uma página de introdução aos potenciais visitantes do site. Ela pode ser assim:\n\n<blockquote>Olá! Eu sou um bike courrier de dia, ator amador à noite e este é meu blog. Eu moro em São Paulo, tenho um cachorro chamado Tonico e eu gosto de caipirinhas. (E de ser pego pela chuva.)</blockquote>\n\nou assim:\n\n<blockquote>A XYZ foi fundada em 1971 e desde então vem proporcionando produtos de qualidade a seus clientes. Localizada em Valinhos, XYZ emprega mais de 2.000 pessoas e faz várias contribuições para a comunidade local.</blockquote>\nComo um novo usuário do WordPress, você deve ir até o <a href=\"http://localhost/adm/blog/wp-admin/\">seu painel</a> para excluir essa página e criar novas páginas com seu próprio conteúdo. Divirta-se!','Página de Exemplo','','inherit','open','open','','2-revision-v1','','','2013-11-20 15:39:27','2013-11-20 15:39:27','',2,'http://localhost/adm/blog/?p=4',0,'revision','',0),(5,1,'2013-11-20 15:49:34','2013-11-20 15:49:34','[wysija_page]','Confirmação de assinatura','','publish','open','open','','subscriptions','','','2013-11-20 15:49:34','2013-11-20 15:49:34','',0,'http://localhost/adm/blog/?wysijap=subscriptions',0,'wysijap','',0),(7,1,'2013-11-20 16:05:58','2013-11-20 16:05:58','','favicon adm\'2','','inherit','open','open','','favicon-adm2-2','','','2013-11-20 16:05:58','2013-11-20 16:05:58','',0,'http://localhost/adm/blog/wp-content/uploads/2013/11/favicon-adm21.png',0,'attachment','image/png',0),(10,1,'2013-11-26 18:57:10','2013-11-26 18:57:10','','Home','','publish','open','open','','inicio','','','2013-12-06 17:28:02','2013-12-06 17:28:02','',0,'http://localhost/adm/blog/?page_id=10',0,'page','',0),(11,1,'2013-11-26 18:56:26','2013-11-26 18:56:26','','Início','','inherit','open','open','','10-revision-v1','','','2013-11-26 18:56:26','2013-11-26 18:56:26','',10,'http://localhost/adm/blog/?p=11',0,'revision','',0),(12,1,'2013-11-26 18:58:12','0000-00-00 00:00:00',' ','','','draft','open','open','','','','','2013-11-26 18:58:12','0000-00-00 00:00:00','',0,'http://localhost/adm/blog/?p=12',1,'nav_menu_item','',0),(13,1,'2013-11-26 19:02:08','2013-11-26 19:02:08','Bem-vindo ao WordPress. Esse é o seu primeiro post. Edite-o ou exclua-o, e então comece a publicar!','Olá, mundo!','','inherit','open','open','','1-revision-v1','','','2013-11-26 19:02:08','2013-11-26 19:02:08','',1,'http://localhost/adm/blog/?p=13',0,'revision','',0),(15,1,'2013-11-26 19:05:32','2013-11-26 19:05:32','Esta é uma página de exemplo. É diferente de um post porque ela ficará em um local e será exibida na navegação do seu site (na maioria dos temas). A maioria das pessoas começa com uma página de introdução aos potenciais visitantes do site. Ela pode ser assim:\r\n<blockquote>Olá! Eu sou um bike courrier de dia, ator amador à noite e este é meu blog. Eu moro em São Paulo, tenho um cachorro chamado Tonico e eu gosto de caipirinhas. (E de ser pego pela chuva.)</blockquote>\r\nou assim:\r\n<blockquote>A XYZ foi fundada em 1971 e desde então vem proporcionando produtos de qualidade a seus clientes. Localizada em Valinhos, XYZ emprega mais de 2.000 pessoas e faz várias contribuições para a comunidade local.</blockquote>\r\nComo um novo usuário do WordPress, você deve ir até o <a href=\"http://localhost/adm/blog/wp-admin/\">seu painel</a> para excluir essa página e criar novas páginas com seu próprio conteúdo. Divirta-se!','Início','','inherit','open','open','','2-revision-v1','','','2013-11-26 19:05:32','2013-11-26 19:05:32','',2,'http://localhost/adm/blog/?p=15',0,'revision','',0),(17,1,'2013-11-26 19:06:10','2013-11-26 19:06:10','Esta é uma página de exemplo. É diferente de um post porque ela ficará em um local e será exibida na navegação do seu site (na maioria dos temas). A maioria das pessoas começa com uma página de introdução aos potenciais visitantes do site. Ela pode ser assim:\r\n<blockquote>Olá! Eu sou um bike courrier de dia, ator amador à noite e este é meu blog. Eu moro em São Paulo, tenho um cachorro chamado Tonico e eu gosto de caipirinhas. (E de ser pego pela chuva.)</blockquote>\r\nou assim:\r\n<blockquote>A XYZ foi fundada em 1971 e desde então vem proporcionando produtos de qualidade a seus clientes. Localizada em Valinhos, XYZ emprega mais de 2.000 pessoas e faz várias contribuições para a comunidade local.</blockquote>\r\nComo um novo usuário do WordPress, você deve ir até o <a href=\"http://localhost/adm/blog/wp-admin/\">seu painel</a> para excluir essa página e criar novas páginas com seu próprio conteúdo. Divirta-se!','Quem somos','','inherit','open','open','','2-revision-v1','','','2013-11-26 19:06:10','2013-11-26 19:06:10','',2,'http://localhost/adm/blog/?p=17',0,'revision','',0),(18,1,'2013-11-26 19:07:24','2013-11-26 19:07:24','<div class=\"separator\" style=\"clear: both; text-align: center;\">\r\n<a href=\"http://3.bp.blogspot.com/_wHURmdXcPVk/TMclq5DGNII/AAAAAAAAA9U/pAcKfWuWwc0/s1600/estrategia.jpg\" imageanchor=\"1\" style=\"margin-left: 1em; margin-right: 1em;\"><img border=\"0\" height=\"230\" src=\"http://3.bp.blogspot.com/_wHURmdXcPVk/TMclq5DGNII/AAAAAAAAA9U/pAcKfWuWwc0/s320/estrategia.jpg\" style=\"background-color: white;\" width=\"320\" /></a></div>\r\n<br />\r\n<h1 style=\"color: #333333; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 32px; font-weight: normal; letter-spacing: -1px; line-height: 32px; margin: 0px; padding: 0px 0px 15px;\">\r\n\r\n<span style=\"background-color: white; font-size: 18px; line-height: 18px;\">Missão:</span></h1>\r\n<div style=\"color: #333333; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; margin-bottom: 1.5em; padding: 0px;\">\r\nDesenvolver Talentos Gerando Resultados.</div>\r\n<ul style=\"color: #333333; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; list-style: none; margin: 0px 0px 15px 15px; padding: 0px;\">\r\n<li style=\"background-image: url(http://admsolucoes.com.br/wp-content/themes/boldy/images/bullet_list.png); background-position: 0px 8px; background-repeat: no-repeat no-repeat; margin: 0px; padding: 5px 0px 5px 25px;\">Oferecendo um ambiente de trabalho desafiador e agregador de valor para os<br style=\"margin: 0px; padding: 0px;\" />membros efetivos, aliando teoria à prática;</li>\r\n<li style=\"background-image: url(http://admsolucoes.com.br/wp-content/themes/boldy/images/bullet_list.png); background-position: 0px 8px; background-repeat: no-repeat no-repeat; margin: 0px; padding: 5px 0px 5px 25px;\">Dando oportunidade aos graduandos em<br style=\"margin: 0px; padding: 0px;\" />Administração de Empresas da UECE de ter um maior conhecimento do mercado;</li>\r\n<li style=\"background-image: url(http://admsolucoes.com.br/wp-content/themes/boldy/images/bullet_list.png); background-position: 0px 8px; background-repeat: no-repeat no-repeat; margin: 0px; padding: 5px 0px 5px 25px;\">Fornecendo aos nossos clientes soluções com qualidade<br style=\"margin: 0px; padding: 0px;\" />em consultoria de gestão empresarial, pesquisa de mercado e<br style=\"margin: 0px; padding: 0px;\" />treinamento a um preço diferenciado;</li>\r\n<li style=\"background-image: url(http://admsolucoes.com.br/wp-content/themes/boldy/images/bullet_list.png); background-position: 0px 8px; background-repeat: no-repeat no-repeat; margin: 0px; padding: 5px 0px 5px 25px;\">Cultivando parceiros efetivos;</li>\r\n<li style=\"background-image: url(http://admsolucoes.com.br/wp-content/themes/boldy/images/bullet_list.png); background-position: 0px 8px; background-repeat: no-repeat no-repeat; margin: 0px; padding: 5px 0px 5px 25px;\">Interagindo com o MEJ a fim de trocar conhecimento e de fortalecê-lo;</li>\r\n<li style=\"background-image: url(http://admsolucoes.com.br/wp-content/themes/boldy/images/bullet_list.png); background-position: 0px 8px; background-repeat: no-repeat no-repeat; margin: 0px; padding: 5px 0px 5px 25px;\">Elevando o nome da UECE no meio acadêmico e empresarial;</li>\r\n<li style=\"background-image: url(http://admsolucoes.com.br/wp-content/themes/boldy/images/bullet_list.png); background-position: 0px 8px; background-repeat: no-repeat no-repeat; margin: 0px; padding: 5px 0px 5px 25px;\">Promovendo ações de responsabilidade sócio-ambiental para a comunidade fortalezense;</li>\r\n<li style=\"background-image: url(http://admsolucoes.com.br/wp-content/themes/boldy/images/bullet_list.png); background-position: 0px 8px; background-repeat: no-repeat no-repeat; margin: 0px; padding: 5px 0px 5px 25px;\">Formando pessoas capazes de causar impacto positivo na sociedade.</li>\r\n</ul>\r\n<h3 style=\"color: #333333; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 18px; letter-spacing: -1px; line-height: 18px; margin: 0px; padding: 10px 0px 15px; text-align: left;\">\r\n\r\nVisão:</h3>\r\n<div style=\"color: #333333; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; margin-bottom: 1.5em; padding: 0px; text-align: left;\">\r\nSer uma empresa júnior reconhecida pela sociedade cearense e lembrada no MEJ</div>\r\n<div style=\"color: #333333; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; margin-bottom: 1.5em; padding: 0px; text-align: left;\">\r\nnacional pelo aprimoramento nos serviços e na gestão interna, tendo, cada vez mais, ex-membros bem posicionados no mercado.</div>\r\n<h3 style=\"color: #333333; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 18px; letter-spacing: -1px; line-height: 18px; margin: 0px; padding: 10px 0px 15px; text-align: left;\">\r\n\r\nValores:</h3>\r\n<div style=\"color: #333333; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; margin-bottom: 1.5em; padding: 0px;\">\r\n<a href=\"http://admsolucoes.com.br/wp-content/uploads/2010/09/referencial-2.png\" style=\"color: #da2128; margin: 0px; padding: 0px; text-decoration: none;\"><img alt=\"\" class=\"size-full wp-image-751 alignright\" height=\"250\" src=\"http://admsolucoes.com.br/wp-content/uploads/2010/09/referencial-2.png\" style=\"border: 0pt none; float: right; height: auto; margin: 5px; max-width: 600px; padding: 5px;\" title=\"referencial-2\" width=\"198\" /></a></div>\r\n<ul style=\"color: #333333; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; list-style: none; margin: 0px 0px 15px 15px; padding: 0px;\">\r\n<li style=\"background-image: url(http://admsolucoes.com.br/wp-content/themes/boldy/images/bullet_list.png); background-position: 0px 8px; background-repeat: no-repeat no-repeat; margin: 0px; padding: 5px 0px 5px 25px;\">Ética</li>\r\n<li style=\"background-image: url(http://admsolucoes.com.br/wp-content/themes/boldy/images/bullet_list.png); background-position: 0px 8px; background-repeat: no-repeat no-repeat; margin: 0px; padding: 5px 0px 5px 25px;\">Comprometimento</li>\r\n<li style=\"background-image: url(http://admsolucoes.com.br/wp-content/themes/boldy/images/bullet_list.png); background-position: 0px 8px; background-repeat: no-repeat no-repeat; margin: 0px; padding: 5px 0px 5px 25px;\">Anseio pelo crescimento pessoal e profissional</li>\r\n<li style=\"background-image: url(http://admsolucoes.com.br/wp-content/themes/boldy/images/bullet_list.png); background-position: 0px 8px; background-repeat: no-repeat no-repeat; margin: 0px; padding: 5px 0px 5px 25px;\">Proatividade</li>\r\n<li style=\"background-image: url(http://admsolucoes.com.br/wp-content/themes/boldy/images/bullet_list.png); background-position: 0px 8px; background-repeat: no-repeat no-repeat; margin: 0px; padding: 5px 0px 5px 25px;\">Qualidade e personalização dos serviços</li>\r\n<li style=\"background-image: url(http://admsolucoes.com.br/wp-content/themes/boldy/images/bullet_list.png); background-position: 0px 8px; background-repeat: no-repeat no-repeat; margin: 0px; padding: 5px 0px 5px 25px;\">Ascensão por mérito</li>\r\n<li style=\"background-image: url(http://admsolucoes.com.br/wp-content/themes/boldy/images/bullet_list.png); background-position: 0px 8px; background-repeat: no-repeat no-repeat; margin: 0px; padding: 5px 0px 5px 25px;\">Orgulho de ser ADM Soluções</li>\r\n</ul>\r\n','Referencial Estratégico','','publish','open','open','','referencial-estrategico','','','2013-12-12 18:28:27','2013-12-12 18:28:27','',0,'http://localhost/adm/blog/?page_id=18',0,'page','',0),(19,1,'2013-11-26 19:07:24','2013-11-26 19:07:24','','Referencial Estratégico','','inherit','open','open','','18-revision-v1','','','2013-11-26 19:07:24','2013-11-26 19:07:24','',18,'http://localhost/adm/blog/?p=19',0,'revision','',0),(20,1,'2013-11-26 19:08:07','2013-11-26 19:08:07','<div class=\"separator\" style=\"clear: both; text-align: center;\">\r\n</div>\r\n<div class=\"separator\" style=\"clear: both; text-align: center;\">\r\n</div>\r\n<div class=\"separator\" style=\"clear: both; text-align: center;\">\r\n</div>\r\n<div class=\"separator\" style=\"clear: both; text-align: center;\">\r\n<a href=\"http://3.bp.blogspot.com/-e7X0hVuJmjc/UPLlGTGHu7I/AAAAAAAABgE/KKzyfl7f0FU/s1600/parceiros.jpg\" imageanchor=\"1\" style=\"margin-left: 1em; margin-right: 1em;\"><img border=\"0\" src=\"http://3.bp.blogspot.com/-e7X0hVuJmjc/UPLlGTGHu7I/AAAAAAAABgE/KKzyfl7f0FU/s1600/parceiros.jpg\" /></a></div>\r\n<br />','Nossos Parceiros','','publish','open','open','','nossos-parceiros','','','2013-12-12 17:25:30','2013-12-12 17:25:30','',0,'http://localhost/adm/blog/?page_id=20',0,'page','',0),(21,1,'2013-11-26 19:08:07','2013-11-26 19:08:07','','Nossos Parceiros','','inherit','open','open','','20-revision-v1','','','2013-11-26 19:08:07','2013-11-26 19:08:07','',20,'http://localhost/adm/blog/?p=21',0,'revision','',0),(22,1,'2013-11-26 19:09:06','2013-11-26 19:09:06','<div class=\"separator\" style=\"clear: both; text-align: center;\"><a href=\"https://www.facebook.com/adm.solucoes\" target=\"_blank\"><img alt=\"\" src=\"http://3.bp.blogspot.com/-3t-_OadZqPg/UAdHBs0QlII/AAAAAAAAAx0/NQvp4wgDR48/s1600/facebook.png\" border=\"0\" /></a><a