INSERT INTO `wp_wysija_email` VALUES (1,1,'Guia de Usuário de 5 Minutos','<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Strict//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd\"><html xmlns=\"http://www.w3.org/1999/xhtml\"><head><meta content=\"text/html; charset=UTF-8\" http-equiv=\"Content-Type\" /><meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\"/><title>Guia de Usuário de 5 Minutos</title><style type=\"text/css\">    .ReadMsgBody { width:100%; }.ExternalClass { width:100%; }.ExternalClass, .ExternalClass p, .ExternalClass span, .ExternalClass font, .ExternalClass td, .ExternalClass div {line-height: 100%;}h1 { font-family:\"Trebuchet MS\"; font-size:40px; line-height:1.3em; color:#000000 !important; font-weight:normal; margin:0; padding:0; font-style:normal; }h2 { font-family:\"Trebuchet MS\"; font-size:30px; line-height:1.2em; color:#424242 !important; font-weight:normal; margin:0; padding:0; font-style:normal; }h3 { font-family:\"Trebuchet MS\"; font-size:24px; line-height:1.1em; color:#424242 !important; font-weight:normal; margin:0; padding:0; font-style:normal; }.yshortcuts { color:#0000FF; }p { font-family:\"Arial\"; margin:1em 0; word-wrap: break-word; }img {outline:none; text-decoration:none; -ms-interpolation-mode: bicubic;}a img {border:none;}#outlook a {padding:0;}body, #wysija_wrapper {    background:#e8e8e8;    color:#000000;    font-family:\"Arial\";    font-size:16px;    -webkit-text-size-adjust:100%;    -ms-text-size-adjust:100%;    }table { border-collapse:collapse; mso-table-lspace:0pt; mso-table-rspace:0pt; }table tr td { border-collapse:collapse; }div, p, a, li, td { -webkit-text-size-adjust:none; -ms-text-size-adjust:none; }#wysija_viewbrowser p, #wysija_viewbrowser a {    font-family: \"Arial\" !important;    font-size: 12px !important;    color: #000000 !important;}#wysija_viewbrowser a, #wysija_unsubscribe a {    text-decoration:underline;}#wysija_unsubscribe p, #wysija_unsubscribe p a {    text-align:center;    color: #000000 !important;    font-size:12px;}a { color:#0000FF; }a:link { color:#0000FF; }a:visited { color:#0000FF; }h1 a { font-family:\"Trebuchet MS\"; font-size:40px; color:#0000FF !important; font-weight:normal; margin:0; padding:0; font-style:normal; }h2 a { font-family:\"Trebuchet MS\"; font-size:30px; color:#0000FF !important; font-weight:normal; margin:0; padding:0; font-style:normal; }h3 a { font-family:\"Trebuchet MS\"; font-size:24px; color:#0000FF !important; font-weight:normal; margin:0; padding:0; font-style:normal; }    </style></head><body bgcolor=\"#e8e8e8\"  style=\"background:#e8e8e8;color:#000000; font-family:\'Arial\';font-size:16px;\" alink=\"#0000FF\" link=\"#0000FF\" text=\"#000000\"><span id=\"wysija_wrapper\" style=\"margin-bottom: 0; margin-left: 0; margin-right: 0; margin-top: 0; padding-bottom: 0; padding-left: 0; padding-right: 0; padding-top: 0; display:block;background:#e8e8e8;\"><table cellspacing=\"0\" id=\"wysija_container\" align=\"center\" style=\"background:none; border:none; border-collapse:collapse; mso-table-lspace:0pt; mso-table-rspace:0pt; border-color:none; border-spacing:0; margin-bottom:0; margin-left:auto; margin-right:auto; margin-top:0; width:600px;\" width=\"600\" cellpadding=\"0\"><tr><td style=\"margin-bottom: 0; margin-left: 0; margin-right: 0; margin-top: 0; padding-bottom: 0; padding-left: 0; padding-right: 0; padding-top: 0;\">                    <table cellspacing=\"0\" id=\"wysija_viewbrowser\" align=\"center\" style=\"background:none; background-color:#e8e8e8; border-collapse: collapse; mso-table-lspace:0pt; mso-table-rspace:0pt; border-spacing: 0; margin-bottom: 0; margin-left: 0; margin-right: 0; margin-top: 0; padding-bottom: 0; padding-left: 0; padding-right: 0; padding-top: 0; width: 600px;\" width=\"600\" cellpadding=\"0\"><tr valign=\"top\"><td id=\"wysija_viewbrowser_content\" style=\"margin-bottom: 0; margin-left: 0; margin-right: 0; margin-top: 0; padding-bottom: 0; padding-left: 0; paddin