}s:8:"category";a:6:{i:0;a:5:{s:4:"data";s:15:"NGFB Open Graph";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:10:"Open Graph";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:2;a:5:{s:4:"data";s:6:"ngfbog";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:3;a:5:{s:4:"data";s:9:"opengraph";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:4;a:5:{s:4:"data";s:12:"twittercards";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:5;a:5:{s:4:"data";s:9:"wordpress";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:30:"http://surniaulula.com/?p=2107";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1792:"<style type="text/css">\r\n.rss-manager table {\r\n    width:955px;\r\n    margin-left:auto;\r\n    margin-right:auto;\r\n}\r\n.rss-manager p {\r\n    text-align:justify;\r\n    margin-top:0px;\r\n    margin-bottom:14px;\r\n}\r\n.rss-manager p.tags, .rss-manager p.categories {\r\n    font-size:0.8em;\r\n    font-style:italic;\r\n}\r\n.rss-manager p.categories {\r\n    margin-bottom:2px;\r\n}\r\n.rss-manager img {\r\n    border:1px solid #ddd;\r\n    background-color:#FFFFFF;\r\n    padding:4px;\r\n}\r\n</style><div class="rss-manager"><table cellspacing="40" align="center"><tr><td valign="top" align="none"><p class="content">There are a lot of plugins for WordPress that add meta tags to webpages, but not all are created equal. ;-) This article will explain what meta tags are, why they are useful, and what makes one set more complete than another. </p><p class="categories">Categories: <a href="http://surniaulul